import React, {useState} from 'react';
import PropTypes from 'prop-types';
import {Alert, Button, Form, FormFeedback, FormGroup, Input, Label} from 'reactstrap';
import {getMultiSelected, repeat} from '../../../utils';
import {isCategoriesValid, isExpiredDateValid, isNameValid, isRatingValid} from './validators';

const ProductForm = (props) => {
    const {product = {}} = props;
    const [name, setName] = useState(product.name || '');
    const [brand, setBrand] = useState(product.brand || '');
    const [rating, setRating] = useState(product.rating || 0);
    const [categories, setCategories] = useState(product.categories || []);
    const [itemsInStock, setItemsInStock] = useState(product.itemsInStock || 0);
    const [receiptDate, setReceiptDate] = useState(product.receiptDate || '');
    const [expirationDate, setExpirationDate] = useState(product.expirationDate || '');
    const [featured, setFeatured] = useState(product.featured || false);
    const [alertSuccess, setAlertSuccess] = useState( false);
    const [alertError, setAlertError] = useState( false);
    let errorTimeout ;

    const clearTimeoutbyId = (timeoutId) => {
    clearTimeout(timeoutId)
}
    const onSubmit = (e) => {

        e.preventDefault();
        if (isNameValid(name) &&
            isCategoriesValid(categories) &&
            isExpiredDateValid(expirationDate) &&
            ((isRatingValid(rating) && featured) || !(isRatingValid(rating) || featured))
            ) {
            props.onSave({
                name,
                brand,
                rating,
                categories,
                itemsInStock,
                receiptDate,
                expirationDate,
                featured,
            });
            setName('') ; setBrand('') ; setRating(0) ; setCategories([])
            setItemsInStock(0) ; setReceiptDate('') ; setExpirationDate('') ; setFeatured(false)
            setAlertSuccess(true);
            const successTimeout = setTimeout( function (){
                setAlertSuccess(false)
            },1500  ) ; clearTimeoutbyId(successTimeout);
        } else {
          setAlertError(true)
           errorTimeout = setTimeout(function (){
                setAlertError(false)
            },2000 , clearTimeoutbyId(errorTimeout))
        }
    }

    return (
        <Form onSubmit={onSubmit}>
            <FormGroup>
                <Label for='name'>Name</Label>
                <Input
                    invalid={!isNameValid(name)}
                    type='text'
                    name='name'
                    id='name'
                    placeholder='Name'
                    value={name}
                    onChange={({target}) => setName(target.value)}
                />
                <FormFeedback>Name is required, the length must not be greater than 200</FormFeedback>
            </FormGroup>
            <FormGroup>
                <Label for='brand'>Brand</Label>
                <Input
                    type='text'
                    name='brand'
                    id='brand'
                    placeholder='Brand'
                    value={brand}
                    onChange={({target}) => setBrand(target.value)}
                />
            </FormGroup>
            <FormGroup>
                <Label for="rating">Rating</Label>
                <Input
                    type="select"
                    name="rating"
                    id="rating"
                    value={rating}
                    onChange={({target}) => setRating(target.value)}
                >
                    {repeat(11).map((v) => (
                        <option key={v} value={v}>{v}</option>
                    ))}

                </Input>
            </FormGroup>
            <FormGroup>
                <Label for="categories">Categories</Label>
                <Input
                    invalid={!isCategoriesValid(categories)}
                    type="select"
                    name="categories"
                    id="categories"
                    multiple
                    value={categories}
                    onChange={({target}) => setCategories(getMultiSelected(target))}
                >
                    {props.categories.map(({id, name}) => (
                        <option key={id} value={id}>{name}</option>
                    ))}
                </Input>
                <FormFeedback>A product must have from 1 to 5 categories</FormFeedback>
            </FormGroup>
            <FormGroup>
                <Label for="itemsInStock">Items In Stock</Label>
                <Input type="number" name="itemsInStock" id="itemsInStock" value={itemsInStock}
                       onChange={({target}) => setItemsInStock(target.value)}
                />
            </FormGroup>
            <FormGroup>
                <Label for="expirationDate">Expiration date</Label>
                <Input
                    type="date"
                    name="expirationDate"
                    id="expirationDate"
                    value={expirationDate}
                    onChange={({target}) => setExpirationDate(target.value)}
                    invalid={!isExpiredDateValid(expirationDate)}

                />
                <FormFeedback>If a product has an expiration date it must expire not less than 30 days since
                    now</FormFeedback>
            </FormGroup>
            <FormGroup>
                <Label for="receiptDate">Receipt date</Label>
                <Input type="date" name="receiptDate" id="receiptDate" value={receiptDate}
                       onChange={({target}) => setReceiptDate(target.value)}
                />
            </FormGroup>
            <FormGroup check>
                <Input id="unique-id"
                       type="checkbox"
                       invalid={!((isRatingValid(rating) && featured) || !(isRatingValid(rating) || featured))}
                       checked={featured}
                       onChange={({target}) => setFeatured(target.checked)}/>
                <Label for="unique-id" check> Featured </Label>
                <FormFeedback>Cocher la case Featured , si le rating est supérieure à 8, sinon la
                    décocher.</FormFeedback>
            </FormGroup>
            <Button>Submit</Button>
            <div style={{width : "40%" , textAlign: "center" ,margin :"auto" }}>
                <Alert  color="danger" isOpen={alertError} toggle={()=>setAlertError(false)}>
                    Please correct all errors and try again
                </Alert>
                <Alert  color="success" isOpen={alertSuccess} toggle={()=>setAlertSuccess(false)}>
                    Success !
                </Alert>
            </div>

        </Form>
    );
}

ProductForm.propTypes = {
    product: PropTypes.object,
    categories: PropTypes.array.isRequired,
    onSave: PropTypes.func.isRequired,
};

export default ProductForm;
